import './App.css';
import React from 'react';
import { Fragment } from 'react/cjs/react.production.min';

const INITIAL_LIST = [
  {
    id: '0',
    title: 'React with RxJS for State Management Tutorial',
    url:
      'https://www.robinwieruch.de/react-rxjs-state-management-tutorial/',
  },
  {
    id: '1',
    title: 'React with Apollo and GraphQL Tutorial',
    url: 'https://www.robinwieruch.de/react-graphql-apollo-tutorial',
  },
];



const App = () => {
  const [ list, setList ] = React.useState(INITIAL_LIST);
  const [ title, setTitle ] = React.useState('');
  const [ url, setUrl] = React.useState('');

  function onRemoveItem(id) {
    const newList = list.filter(item => item.id !== id)
    setList(newList);
  }
  function handleAdd() {
    const newItem = {id: `${list.length}`, title: title, url: url}
    const newList = list.concat(newItem);
    setList(newList);

  }

  function handleChange(event) {
    setTitle(event.target.value)
  }

  function handleChange2(event) {
    setUrl(event.targe.value)
  }

  return (
    <Fragment>
      <div>
        <input type="text" value={title} placeholder='title' onChange={() => handleChange()}/>
        <input type="text" value={url} placeholder='url' onChange={() => handleChange2()}/>
        <button type='button' onClick={handleAdd}>Add new link</button>
      </div>
      <ul>
        {list.map(item => (
          <li key={item.id}>
            <a href={item.url}>{item.title}</a>
            <button type='button' onClick={() => onRemoveItem(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </Fragment>
  );
}

export default App;
